import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

export default new Vuex.Store({
  state() {
    return {
      messages: [],
    };
  },
  mutations: {
    async setMessages(state, data) {
      state.messages = data;
    },
    addMessage(state) {
      if (state.messages.length === 0) {
        state.messages.push({
          id: 4,
          voice: "",
          text: "",
        });
      } else {
        const newId = state.messages[state.messages.length - 1].id + 1;
        state.messages.push({
          id: newId,
          voice: "",
          text: "",
        });
      }
    },
    deleteMessage(state, id) {
      const index = state.messages.findIndex((message) => message.id === id);
      state.messages.splice(index, 1);
    },
    updateMessageVoice(state, target) {
      const { id, value } = target;
      const idValue = id.split("voice-id-")[1];
      const indexToUpdate = state.messages.findIndex(
        (message) => message.id === Number(idValue)
      );
      state.messages[indexToUpdate].voice = value;
    },
    updateMessageText(state, target) {
      const { id, value } = target;
      const idValue = id.split("text-id-")[1];
      const indexToUpdate = state.messages.findIndex(
        (message) => message.id === Number(idValue)
      );
      state.messages[indexToUpdate].text = value;
    },
    uploadMessages(state, status) {
      if (status === 200) {
        alert("Messages uploaded successfully");
      } else {
        alert(
          "An error has occured while uploading the data, please try again later."
        );
      }
    },
  },
  actions: {
    async getMessages({ commit }) {
      try {
        const res = await fetch(
          "http://www.mocky.io/v2/5ae1c5792d00004d009d7e5c"
        );
        const data = await res.json();
        commit("setMessages", data);
      } catch (error) {
        alert(
          "An error has occured while getting the data, please try again later."
        );
        commit("setMessages", []);
      }
    },
    async uploadMessages({ commit, state }) {
      try {
        const res = await fetch(
          "http://www.mocky.io/v2/5ae1c5792d00004d009d7e5c",
          {
            method: "POST",
            body: JSON.stringify(state.messages),
          }
        );
        commit("uploadMessages", res.status);
      } catch (error) {
        alert(
          "An error has occured while uploading the data, please try again later."
        );
      }
    },
  },
  getters: {},
  modules: {},
});
