import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex);

async function fetchData() {
  const res = await fetch("http://www.mocky.io/v2/5ae1c5792d00004d009d7e5c");
  const data = await res.json();
  return data;
}

export default new Vuex.Store({
  state() {
    return {
      data: [],
    };
  },
  getters: {},
  mutations: {
    getData(state) {
      const data = fetchData();
      state.data = data;
      console.log(this.$store.state.data);
    },
  },
  actions: {},
  modules: {},
});
